@js:
var html=result,
options={headers:{Referer:baseUrl}};

(function getImgList() {
    eval(html.match(/(var qTcms_Cur=[\S\s]+var qTcms_S_show_1=.*?;)/)[1]);
    var list = java.base64Decode(qTcms_S_m_murl_e).split("\\$qingtiandy\\$");
    var piclist = new Array();
    for (var i = 0; i < list.length; i++) {
        var v = list[i];
        var s = v;
        if (v.substring(0, 1) == "/") {
            s = qTcms_m_weburl + s;
        } else {
            if (qTcms_Pic_m_if != "2") {
                v = v.replaceAll("\\?", "a1a1");
